import React, { useState } from 'react';
import HomeScreen from './HomeScreen';
import ProductDetail from './ProductDetail';
import NewProductForm from './NewProductForm';

function App() {
  const [traceId, setTraceId] = useState('');
  const [showDetails, setShowDetails] = useState(false);
  const [products, setProducts] = useState([
    {
      id: 'ABC123456789',
      name: 'Sample Product',
      origin: 'Australia',
      status: 'Certified',
      history: [
        'Manufactured: 2024-06-01',
        'Shipped: 2024-06-10',
        'Received: 2024-06-15'
      ]
    }
  ]);
  const [showNewProductForm, setShowNewProductForm] = useState(false);

  const findProduct = (id) => products.find(p => p.id === id);

  const handleManualSubmit = (e) => {
    e.preventDefault();
    if (traceId.trim()) {
      if (findProduct(traceId.trim())) {
        setShowDetails(true);
        setShowNewProductForm(false);
      } else {
        setShowNewProductForm(true);
        setShowDetails(false);
      }
    }
  };

  const handleBarcodeScan = () => {
    const simulatedBarcode = 'NEW987654321';
    setTraceId(simulatedBarcode);
    if (findProduct(simulatedBarcode)) {
      setShowDetails(true);
      setShowNewProductForm(false);
    } else {
      setShowNewProductForm(true);
      setShowDetails(false);
    }
  };

  const handleBack = () => {
    setShowDetails(false);
    setShowNewProductForm(false);
    setTraceId('');
  };

  const handleSaveNewProduct = (newProduct) => {
    setProducts([...products, newProduct]);
    setShowDetails(true);
    setShowNewProductForm(false);
  };

  return (
    <div>
      <h1>Trace360â„¢</h1>
      <HomeScreen />
      <div style={{ marginTop: 32 }}>
        {!showDetails && !showNewProductForm ? (
          <>
            <h2>Trace Scanner</h2>
            <button onClick={handleBarcodeScan}>Scan Barcode (Simulated)</button>
            <form onSubmit={handleManualSubmit} style={{ marginTop: 16 }}>
              <label>
                Or enter Trace ID manually:
                <input
                  type="text"
                  value={traceId}
                  onChange={(e) => setTraceId(e.target.value)}
                  placeholder="e.g. 123456789"
                  style={{ marginLeft: 8 }}
                />
              </label>
              <button type="submit" style={{ marginLeft: 8 }}>Submit</button>
            </form>
          </>
        ) : showDetails ? (
          <>
            <ProductDetail traceId={traceId} product={findProduct(traceId)} />
            <button onClick={handleBack} style={{ marginTop: 16 }}>Back to Scanner</button>
          </>
        ) : (
          <>
            <NewProductForm
              traceId={traceId}
              onSave={handleSaveNewProduct}
              onCancel={handleBack}
            />
          </>
        )}
      </div>
    </div>
  );
}

export default App;
