import React, { useState } from 'react';

function NewProductForm({ traceId, onSave, onCancel, initialData }) {
  const [farmName, setFarmName] = useState(initialData?.farmName || '');
  const [organicCertified, setOrganicCertified] = useState(initialData?.organicCertified || '');
  const [dateSow, setDateSow] = useState(initialData?.dateSow || '');
  const [seedVariety, setSeedVariety] = useState(initialData?.seedVariety || '');
  const [grownFor, setGrownFor] = useState(initialData?.grownFor || '');
  const [dateHarvested, setDateHarvested] = useState(initialData?.dateHarvested || '');
  const [thc, setThc] = useState(initialData?.thc || '');
  const [currentStatus, setCurrentStatus] = useState(initialData?.currentStatus || '');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({
      id: traceId,
      farmName,
      organicCertified,
      dateSow,
      seedVariety,
      grownFor,
      dateHarvested,
      thc,
      currentStatus
    });
  };

  return (
    <div>
      <h2>{initialData ? 'Edit Product' : 'New Product Entry'}</h2>
      <form className="product-form" onSubmit={handleSubmit}>
        <p><strong>Trace ID:</strong> {traceId}</p>
        <label>
          Growing Locations:
          <input value={farmName} onChange={e => setFarmName(e.target.value)} />
        </label>
        <label>
          Organic Certified:
          <select value={organicCertified} onChange={e => setOrganicCertified(e.target.value)}>
            <option value="">Select</option>
            <option value="Yes">Yes</option>
            <option value="No">No</option>
          </select>
        </label>
        <label>
          Planted Date:
          <input type="date" value={dateSow} onChange={e => setDateSow(e.target.value)} />
        </label>
        <label>
          Seed Variety:
          <input value={seedVariety} onChange={e => setSeedVariety(e.target.value)} />
        </label>
        <label>
          Grown for:
          <input value={grownFor} onChange={e => setGrownFor(e.target.value)} />
        </label>
        <label>
          Harvested Date:
          <input type="date" value={dateHarvested} onChange={e => setDateHarvested(e.target.value)} />
        </label>
        <label>
          THC compliance level:
          <input value={thc} onChange={e => setThc(e.target.value)} />
        </label>
        <label>
          Current Status:
          <input value={currentStatus} onChange={e => setCurrentStatus(e.target.value)} />
        </label>
        <button type="submit">{initialData ? 'Update Product' : 'Save Product'}</button>
        <button type="button" onClick={onCancel} style={{ marginLeft: 8 }}>Cancel</button>
      </form>
    </div>
  );
}
export default NewProductForm;
