import React, { useState } from 'react';
import HomeScreen from './HomeScreen';
import ProductDetail from './ProductDetail';
import NewProductForm from './NewProductForm';
import logo from './logo.png';

function App() {
  const [traceId, setTraceId] = useState('');
  const [showDetails, setShowDetails] = useState(false);
  const [products, setProducts] = useState([]);
  const [showNewProductForm, setShowNewProductForm] = useState(false);
  const [editProduct, setEditProduct] = useState(null);

  const findProduct = (id) => products.find(p => p.id === id);

  const handleManualSubmit = (e) => {
    e.preventDefault();
    if (traceId.trim()) {
      const found = findProduct(traceId.trim());
      if (found) {
        setShowDetails(true);
        setShowNewProductForm(false);
        setEditProduct(null);
      } else {
        setShowNewProductForm(true);
        setShowDetails(false);
        setEditProduct(null);
      }
    }
  };

  const handleBarcodeScan = () => {
    const simulatedBarcode = 'NEW987654321';
    setTraceId(simulatedBarcode);
    const found = findProduct(simulatedBarcode);
    if (found) {
      setShowDetails(true);
      setShowNewProductForm(false);
      setEditProduct(null);
    } else {
      setShowNewProductForm(true);
      setShowDetails(false);
      setEditProduct(null);
    }
  };

  const handleBack = () => {
    setShowDetails(false);
    setShowNewProductForm(false);
    setEditProduct(null);
    setTraceId('');
  };

  const handleSaveNewProduct = (newProduct) => {
    if (editProduct) {
      setProducts(products.map(p => (p.id === newProduct.id ? newProduct : p)));
    } else {
      setProducts([...products, newProduct]);
    }
    setShowDetails(true);
    setShowNewProductForm(false);
    setEditProduct(null);
  };

  const handleEdit = () => {
    const product = findProduct(traceId);
    setEditProduct(product);
    setShowNewProductForm(true);
    setShowDetails(false);
  };

  return (
    <div>
      <img src={logo} alt="Company Logo" style={{ height: 60, marginBottom: 16 }} />
      <HomeScreen />
      <div style={{ marginTop: 32 }}>
        {!showDetails && !showNewProductForm ? (
          <>
            <h2>Trace Scanner</h2>
            <button onClick={handleBarcodeScan}>Scan Barcode (Simulated)</button>
            <form onSubmit={handleManualSubmit} style={{ marginTop: 16 }}>
              <label>
                Or enter Trace ID manually:
                <input
                  type="text"
                  value={traceId}
                  onChange={(e) => setTraceId(e.target.value)}
                  placeholder="e.g. 123456789"
                  style={{ marginLeft: 8 }}
                />
              </label>
              <button type="submit" style={{ marginLeft: 8 }}>Submit</button>
            </form>
          </>
        ) : showDetails ? (
          <>
            <ProductDetail product={findProduct(traceId)} onEdit={handleEdit} />
            <button onClick={handleBack} style={{ marginTop: 16 }}>Back to Scanner</button>
          </>
        ) : (
          <>
            <NewProductForm
              traceId={traceId}
              onSave={handleSaveNewProduct}
              onCancel={handleBack}
              initialData={editProduct}
            />
          </>
        )}
      </div>
    </div>
  );
}

export default App;
