import React, { useState } from 'react';

function NewProductForm({ traceId, onSave, onCancel, initialData }) {
  const [seedVariety, setSeedVariety] = useState(initialData?.seedVariety || '');
  const [dateSow, setDateSow] = useState(initialData?.dateSow || '');
  const [locationGrown, setLocationGrown] = useState(initialData?.locationGrown || '');
  const [dateHarvested, setDateHarvested] = useState(initialData?.dateHarvested || '');
  const [thc, setThc] = useState(initialData?.thc || '');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({
      id: traceId,
      seedVariety,
      dateSow,
      locationGrown,
      dateHarvested,
      thc
    });
  };

  return (
    <div>
      <h2>{initialData ? 'Edit Product' : 'New Product Entry'}</h2>
      <form onSubmit={handleSubmit}>
        <p><strong>Trace ID:</strong> {traceId}</p>
        <label>
          Seed Variety:
          <input value={seedVariety} onChange={e => setSeedVariety(e.target.value)} required />
        </label>
        <br />
        <label>
          Date of Sow:
          <input type="date" value={dateSow} onChange={e => setDateSow(e.target.value)} required />
        </label>
        <br />
        <label>
          Location Grown:
          <input value={locationGrown} onChange={e => setLocationGrown(e.target.value)} required />
        </label>
        <br />
        <label>
          Date Harvested:
          <input type="date" value={dateHarvested} onChange={e => setDateHarvested(e.target.value)} required />
        </label>
        <br />
        <label>
          THC%:
          <input type="number" step="0.01" value={thc} onChange={e => setThc(e.target.value)} required />
        </label>
        <br />
        <button type="submit">{initialData ? 'Update Product' : 'Save Product'}</button>
        <button type="button" onClick={onCancel} style={{ marginLeft: 8 }}>Cancel</button>
      </form>
    </div>
  );
}

export default NewProductForm;
